"use strict";window.addEventListener("DOMContentLoaded",()=>{const menuBtn=document.querySelector("#menu-btn"),menu=document.querySelector("#menu");menuBtn.addEventListener("click",e=>{e.stopPropagation(),menu.classList[menu.classList.contains("show")?"remove":"add"]("show")}),document.addEventListener("click",e=>{menu.contains(e.target)||menu.classList.remove("show")}),document.querySelector("#current-year").textContent=(new Date).getFullYear();const transNav=document.querySelector("#trans-nav");if(transNav){const navLogo=transNav.querySelector("#logo"),navLogoSrcs={transparent:"/assets/logo-under-dark.svg",white:"/assets/logo.svg"};transNav.toTransparent=()=>{transNav.classList.add("transparent"),navLogo.src=navLogoSrcs.transparent},transNav.toWhite=()=>{transNav.classList.remove("transparent"),navLogo.src=navLogoSrcs.white},window.addEventListener("scroll",()=>{document.scrollingElement.scrollTop>=200?transNav.toWhite():transNav.toTransparent()})}AOS.init()});