"use strict";window.addEventListener("DOMContentLoaded",()=>{const handleProductGalleryActions=product=>{const gallery=document.getElementById(product.dataset.galleryId),openGalleryBtn=product.querySelector(".open-gallery-btn"),closeGalleryBtn=gallery&&gallery.querySelector(".close-gallery-btn");if(gallery&&openGalleryBtn&&closeGalleryBtn){const openGallery=()=>{gallery.classList.add("show"),document.documentElement.style.overflowY="hidden"},closeGallery=()=>{gallery.classList.remove("show"),document.documentElement.style.overflowY="auto"};openGalleryBtn.addEventListener("click",openGallery),closeGalleryBtn.addEventListener("click",closeGallery)}},products=document.querySelectorAll("[data-gallery-id]");products.forEach(product=>handleProductGalleryActions(product))});